mapper.update:
UPDATE item_list_statis
SET `count` = `count` + #{param2}, price = price + #{param3} * #{param2}, `upDate` = UNIX_TIMESTAMP()
WHERE id = #{param1}

UPDATE item_list_statis
SET exData = JSON_MERGE(exData, #{param2}), `upDate` = UNIX_TIMESTAMP()
WHERE itemType = #{param1}

UPDATE item_list_statis
SET `count` = `count` - #{param4}, price = price - #{param5}
WHERE `name` = #{param1} AND itemType = #{param2} AND manufactor = #{param3} AND
exData = #{param6}
