mapper.update:
UPDATE item_list
SET ex_data = JSON_MERGE(ex_data, #{param2}), up_date = UNIX_TIMESTAMP()
WHERE item_type = #{param1}

UPDATE item_list
SET remain = remain - #{param2}
WHERE id = #{param1};

mapper.delete:
UPDATE item_list
SET is_alive = 0
WHERE id = #{param1};

mapper.select:
SELECT item_list.id, item_list.name, item_type.name AS item_type, item_list.model, item_list.count, item_list.remain, item_list.price, item_list.ins_date, item_list.up_date FROM item_list LEFT JOIN item_type ON item_list.item_type = item_type.id WHERE item_list.name = #{param1} AND item_list.item_type = #{param2} AND item_list.manufactor = #{param3} AND item_list.ex_data = #{param4}::jsonb AND item_list.remain > 0 AND item_list.is_alive = 1 AND item_list.id NOT IN(0
, #{item}
) LIMIT 1
