mapper.select:
SELECT statis.name, item_type.name AS item_type, manu.name AS manufactor,
SUM(statis.count) AS "count", SUM(statis.price) AS price, statis.up_date FROM item_list_statis AS statis, item_type, manufactor AS manu WHERE statis.item_type = item_type.id AND statis.manufactor = manu.id
AND statis.item_type=#{type}
AND statis.name LIKE #{kw}
AND statis.manufactor=#{manu}
GROUP BY statis.name, statis.manufactor, item_type.name, manu.name, statis.up_date, statis.id ORDER BY statis.up_date DESC,statis.id
statis.count, statis.price, statis.ex_data, statis.up_date FROM item_list_statis AS statis, item_type, manufactor AS manu WHERE statis.item_type = item_type.id AND statis.manufactor = manu.id AND statis.item_type=#{type}
AND statis.name ILIKE #{kw}
AND statis.manufactor=#{manu}
AND statis.ex_data @>#{exData}::jsonb ORDER BY statis.up_date DESC,statis.id

SELECT item.id, item.name, item_type.name AS item_type, manu.name AS manufactor, item.count, item.remain, item.price, item.ex_data, item.desc, item.ins_date FROM item_list AS item, item_type, manufactor AS manu WHERE item.item_type=item_type.id AND item.manufactor=manu.id
AND item.item_type=#{type}
AND item.name ILIKE #{kw}
AND manu.name=#{manu}
AND item.ex_data @> #{exData}::jsonb
AND item.ins_date BETWEEN #{startTime} AND #{endTime}
AND item.ins_date >= #{startTime}
AND item.ins_date <= #{endTime}
ORDER BY item.ins_date DESC
